var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
if (!data.nom) throw new Error('nom requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var cols = ['tenant_id', 'nom'];
var vals = ["'" + esc(tenantId) + "'", "'" + esc(data.nom) + "'"];
if (data.description) { cols.push('description'); vals.push("'" + esc(data.description) + "'"); }
if (data.clientId) { cols.push('client_id'); vals.push("'" + esc(data.clientId) + "'"); }
if (data.responsableId) { cols.push('responsable_id'); vals.push("'" + esc(data.responsableId) + "'"); }
if (data.statut) { cols.push('statut'); vals.push("'" + esc(data.statut) + "'"); }
if (data.dateDebut) { cols.push('date_debut'); vals.push("'" + esc(data.dateDebut) + "'"); }
if (data.dateFin) { cols.push('date_fin'); vals.push("'" + esc(data.dateFin) + "'"); }
if (data.budgetPrevu) { cols.push('budget_prevu'); vals.push(parseFloat(data.budgetPrevu)); }
if (data.progression !== undefined) { cols.push('progression'); vals.push(parseFloat(data.progression)); }
if (data.couleur) { cols.push('couleur'); vals.push("'" + esc(data.couleur) + "'"); }
if (data.tags) { cols.push('tags'); vals.push("'" + esc(JSON.stringify(data.tags)) + "'"); }
if (data.notes) { cols.push('notes'); vals.push("'" + esc(data.notes) + "'"); }

var query = "INSERT INTO projets (" + cols.join(', ') + ") VALUES (" + vals.join(', ') + ") RETURNING *";
return [{json: {query}}];

var rows = $input.first().json;
if (!rows || rows.length === 0) return [{json: {success: false, error: 'Création échouée'}}];
return [{json: {success: true, data: rows[0]}}];
