var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
if (!data.projetId || !UUID_RE.test(String(data.projetId))) throw new Error('projetId requis');
if (!data.titre) throw new Error('titre requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var cols = ['tenant_id', 'projet_id', 'titre'];
var vals = ["'" + esc(tenantId) + "'", "'" + esc(data.projetId) + "'", "'" + esc(data.titre) + "'"];
if (data.description) { cols.push('description'); vals.push("'" + esc(data.description) + "'"); }
if (data.assigneA) { cols.push('assigne_a'); vals.push("'" + esc(data.assigneA) + "'"); }
if (data.priorite) { cols.push('priorite'); vals.push("'" + esc(data.priorite) + "'"); }
if (data.statut) { cols.push('statut'); vals.push("'" + esc(data.statut) + "'"); }
if (data.dateEcheance) { cols.push('date_echeance'); vals.push("'" + esc(data.dateEcheance) + "'"); }
if (data.heuresEstimees !== undefined) { cols.push('heures_estimees'); vals.push(parseFloat(data.heuresEstimees)); }
if (data.heuresReelles !== undefined) { cols.push('heures_reelles'); vals.push(parseFloat(data.heuresReelles)); }
if (data.ordre !== undefined) { cols.push('ordre'); vals.push(parseFloat(data.ordre)); }
if (data.parentId) { cols.push('parent_id'); vals.push("'" + esc(data.parentId) + "'"); }

var query = "INSERT INTO projet_taches (" + cols.join(', ') + ") VALUES (" + vals.join(', ') + ") RETURNING *";
return [{json: {query}}];

var rows = $input.first().json;
if (!rows || rows.length === 0) return [{json: {success: false, error: 'Création échouée'}}];
return [{json: {success: true, data: rows[0]}}];
