var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
if (!data.id || !UUID_RE.test(String(data.id))) throw new Error('ID requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var sets = [];
if (data.titre) sets.push("titre = '" + esc(data.titre) + "'");
if (data.description !== undefined) sets.push("description = '" + esc(data.description) + "'");
if (data.assigneA) sets.push("assigne_a = '" + esc(data.assigneA) + "'");
if (data.priorite) sets.push("priorite = '" + esc(data.priorite) + "'");
if (data.statut) sets.push("statut = '" + esc(data.statut) + "'");
if (data.dateEcheance) sets.push("date_echeance = '" + esc(data.dateEcheance) + "'");
if (data.heuresEstimees !== undefined) sets.push("heures_estimees = " + parseFloat(data.heuresEstimees));
if (data.heuresReelles !== undefined) sets.push("heures_reelles = " + parseFloat(data.heuresReelles));
if (data.ordre !== undefined) sets.push("ordre = " + parseFloat(data.ordre));
if (data.parentId) sets.push("parent_id = '" + esc(data.parentId) + "'");
sets.push("updated_at = NOW()");

if (sets.length === 1) throw new Error('Aucun champ à mettre à jour');
var query = "UPDATE projet_taches SET " + sets.join(', ') + " WHERE id = '" + esc(data.id) + "' AND tenant_id = '" + esc(tenantId) + "' RETURNING *";
return [{json: {query}}];

var rows = $input.first().json;
if (!rows || rows.length === 0) return [{json: {success: false, error: 'Mise à jour échouée'}}];
return [{json: {success: true, data: rows[0]}}];
