var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
if (!data.id || !UUID_RE.test(String(data.id))) throw new Error('ID requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var sets = [];
if (data.nom) sets.push("nom = '" + esc(data.nom) + "'");
if (data.description !== undefined) sets.push("description = '" + esc(data.description) + "'");
if (data.clientId) sets.push("client_id = '" + esc(data.clientId) + "'");
if (data.responsableId) sets.push("responsable_id = '" + esc(data.responsableId) + "'");
if (data.statut) sets.push("statut = '" + esc(data.statut) + "'");
if (data.dateDebut) sets.push("date_debut = '" + esc(data.dateDebut) + "'");
if (data.dateFin) sets.push("date_fin = '" + esc(data.dateFin) + "'");
if (data.budgetPrevu !== undefined) sets.push("budget_prevu = " + parseFloat(data.budgetPrevu));
if (data.budgetConsomme !== undefined) sets.push("budget_consomme = " + parseFloat(data.budgetConsomme));
if (data.progression !== undefined) sets.push("progression = " + parseFloat(data.progression));
if (data.couleur) sets.push("couleur = '" + esc(data.couleur) + "'");
if (data.tags) sets.push("tags = '" + esc(JSON.stringify(data.tags)) + "'");
if (data.notes !== undefined) sets.push("notes = '" + esc(data.notes) + "'");
sets.push("updated_at = NOW()");

if (sets.length === 1) throw new Error('Aucun champ à mettre à jour');
var query = "UPDATE projets SET " + sets.join(', ') + " WHERE id = '" + esc(data.id) + "' AND tenant_id = '" + esc(tenantId) + "' RETURNING *";
return [{json: {query}}];

var rows = $input.first().json;
if (!rows || rows.length === 0) return [{json: {success: false, error: 'Mise à jour échouée'}}];
return [{json: {success: true, data: rows[0]}}];
