var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var query = "SELECT " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "') as total_projets, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'brouillon') as projets_brouillon, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'planifie') as projets_planifie, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'en_cours') as projets_en_cours, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'en_pause') as projets_en_pause, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'termine') as projets_termine, " +
  "(SELECT COUNT(*) FROM projets WHERE tenant_id = '" + esc(tenantId) + "' AND statut = 'annule') as projets_annule, " +
  "(SELECT COALESCE(SUM(budget_prevu), 0) FROM projets WHERE tenant_id = '" + esc(tenantId) + "') as total_budget_prevu, " +
  "(SELECT COALESCE(SUM(budget_consomme), 0) FROM projets WHERE tenant_id = '" + esc(tenantId) + "') as total_budget_consomme, " +
  "(SELECT COALESCE(AVG(progression), 0) FROM projets WHERE tenant_id = '" + esc(tenantId) + "') as avg_progression";

return [{json: {query}}];

var rows = $input.first().json;
if (!rows || rows.length === 0) return [{json: {success: false, error: 'Impossible de récupérer les données'}}];
var data = rows[0];
return [{json: {success: true, dashboard: {total_projets: parseInt(data.total_projets) || 0, by_statut: {brouillon: parseInt(data.projets_brouillon) || 0, planifie: parseInt(data.projets_planifie) || 0, en_cours: parseInt(data.projets_en_cours) || 0, en_pause: parseInt(data.projets_en_pause) || 0, termine: parseInt(data.projets_termine) || 0, annule: parseInt(data.projets_annule) || 0}, budget: {total_prevu: parseFloat(data.total_budget_prevu) || 0, total_consomme: parseFloat(data.total_budget_consomme) || 0}, avg_progression: parseFloat(data.avg_progression) || 0}}}];
