var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis');
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }

var where = "WHERE tenant_id = '" + esc(tenantId) + "'";
if (data.statut) where += " AND statut = '" + esc(data.statut) + "'";
if (data.clientId) where += " AND client_id = '" + esc(data.clientId) + "'";
if (data.responsableId) where += " AND responsable_id = '" + esc(data.responsableId) + "'";
if (data.search) where += " AND nom ILIKE '%" + esc(data.search) + "%'";

var query = "SELECT * FROM projets " + where + " ORDER BY created_at DESC";
return [{json: {query}}];
