var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var id = require('crypto').randomUUID();
var now = new Date().toISOString();
var memberId = data.memberId || '';
var mois = data.mois || 1;
var annee = data.annee || new Date().getFullYear();
var salaireBase = data.salaireBase || 0;
var primes = data.primes || 0;
var deductions = data.deductions || 0;
var chargesPatronales = data.chargesPatronales || 0;
var chargesSalariales = data.chargesSalariales || 0;
var netAPayer = data.netAPayer || 0;
var netImposable = data.netImposable || 0;
var statut = data.statut || 'brouillon';
var commentaire = data.commentaire || '';

var query = "INSERT INTO rh_bulletins_paie (id, tenant_id, member_id, mois, annee, salaire_base, primes, deductions, charges_patronales, charges_salariales, net_a_payer, net_imposable, statut, commentaire, created_at, updated_at) VALUES ('" + esc(id) + "', '" + esc(tenantId) + "', '" + esc(memberId) + "', " + mois + ", " + annee + ", " + salaireBase + ", " + primes + ", " + deductions + ", " + chargesPatronales + ", " + chargesSalariales + ", " + netAPayer + ", " + netImposable + ", '" + esc(statut) + "', '" + esc(commentaire) + "', '" + esc(now) + "', '" + esc(now) + "') RETURNING *;";

return [{json: {query: query}}];

const row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, error: 'Creation failed'}}];
}
return [{json: {success: true, data: {
  id: row.id,
  memberId: row.member_id,
  mois: Number(row.mois || 0),
  annee: Number(row.annee || 0),
  salaireBase: Number(row.salaire_base || 0),
  primes: Number(row.primes || 0),
  deductions: Number(row.deductions || 0),
  chargesPatronales: Number(row.charges_patronales || 0),
  chargesSalariales: Number(row.charges_salariales || 0),
  netAPayer: Number(row.net_a_payer || 0),
  netImposable: Number(row.net_imposable || 0),
  statut: row.statut,
  commentaire: row.commentaire,
  createdAt: row.created_at,
  updatedAt: row.updated_at
}}}];
