var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var id = String(data.id || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}
if (!uuidRegex.test(id)) {
  throw new Error('Invalid id');
}

var now = new Date().toISOString();
var setParts = ["updated_at = '" + esc(now) + "'"];

if (data.memberId !== undefined) setParts.push("member_id = '" + esc(data.memberId) + "'");
if (data.mois !== undefined) setParts.push("mois = " + parseInt(data.mois));
if (data.annee !== undefined) setParts.push("annee = " + parseInt(data.annee));
if (data.salaireBase !== undefined) setParts.push("salaire_base = " + (data.salaireBase || 0));
if (data.primes !== undefined) setParts.push("primes = " + (data.primes || 0));
if (data.deductions !== undefined) setParts.push("deductions = " + (data.deductions || 0));
if (data.chargesPatronales !== undefined) setParts.push("charges_patronales = " + (data.chargesPatronales || 0));
if (data.chargesSalariales !== undefined) setParts.push("charges_salariales = " + (data.chargesSalariales || 0));
if (data.netAPayer !== undefined) setParts.push("net_a_payer = " + (data.netAPayer || 0));
if (data.netImposable !== undefined) setParts.push("net_imposable = " + (data.netImposable || 0));
if (data.statut !== undefined) setParts.push("statut = '" + esc(data.statut) + "'");
if (data.commentaire !== undefined) setParts.push("commentaire = '" + esc(data.commentaire) + "'");

var query = "UPDATE rh_bulletins_paie SET " + setParts.join(', ') + " WHERE tenant_id = '" + esc(tenantId) + "' AND id = '" + esc(id) + "' RETURNING *;";

return [{json: {query: query}}];

const row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, error: 'Not found'}}];
}
return [{json: {success: true, data: {
  id: row.id,
  memberId: row.member_id,
  mois: Number(row.mois || 0),
  annee: Number(row.annee || 0),
  salaireBase: Number(row.salaire_base || 0),
  primes: Number(row.primes || 0),
  deductions: Number(row.deductions || 0),
  chargesPatronales: Number(row.charges_patronales || 0),
  chargesSalariales: Number(row.charges_salariales || 0),
  netAPayer: Number(row.net_a_payer || 0),
  netImposable: Number(row.net_imposable || 0),
  statut: row.statut,
  commentaire: row.commentaire,
  createdAt: row.created_at,
  updatedAt: row.updated_at
}}}];
