var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var id = String(data.id || '');
var userId = String(data.userId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

if (!id || !userId) {
  throw new Error('id and userId are required');
}

var query = 'UPDATE rh_conges SET statut = \'approuve\', approuve_par = \'' + esc(userId) + '\', date_reponse = NOW() WHERE id = \'' + esc(id) + '\' AND tenant_id = \'' + esc(tenantId) + '\' RETURNING *';

return {query: query};

var row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, message: 'Not found'}}];
}

var item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  joursPris: Number(row.jours_pris || 0),
  motif: row.motif,
  statut: row.statut,
  approuvePar: row.approuve_par,
  dateReponse: row.date_reponse,
  commentaire: row.commentaire,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: item}}];
