var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

if (!data.memberId || !data.type || !data.dateDebut || !data.dateFin) {
  throw new Error('memberId, type, dateDebut, dateFin are required');
}

var query = 'INSERT INTO rh_conges (tenant_id, member_id, type, date_debut, date_fin, jours_pris, motif, statut, approuve_par, date_reponse, commentaire) VALUES (' +
  '\'' + esc(tenantId) + '\',' +
  '\'' + esc(data.memberId) + '\',' +
  '\'' + esc(data.type) + '\',' +
  '\'' + esc(data.dateDebut) + '\',' +
  '\'' + esc(data.dateFin) + '\',' +
  (data.joursPris != null ? esc(data.joursPris) : 'NULL') + ',' +
  (data.motif ? '\'' + esc(data.motif) + '\'' : 'NULL') + ',' +
  '\'' + esc(data.statut || 'en_attente') + '\',' +
  (data.approuvePar ? '\'' + esc(data.approuvePar) + '\'' : 'NULL') + ',' +
  (data.dateReponse ? '\'' + esc(data.dateReponse) + '\'' : 'NULL') + ',' +
  (data.commentaire ? '\'' + esc(data.commentaire) + '\'' : 'NULL') +
  ') RETURNING *';

return {query: query};

const rows = $input.first().json.queryResults || [];

if (!rows || rows.length === 0) {
  return [{json: {success: false, message: 'Creation failed'}}];
}

var row = rows[0];
var item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  joursPris: row.jours_pris,
  motif: row.motif,
  statut: row.statut,
  approuvePar: row.approuve_par,
  dateReponse: row.date_reponse,
  commentaire: row.commentaire,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: item}}];
