var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var id = String(data.id || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

if (!id) {
  throw new Error('id is required');
}

var updates = [];
if (data.memberId != null) updates.push('member_id = \'' + esc(data.memberId) + '\'');
if (data.type != null) updates.push('type = \'' + esc(data.type) + '\'');
if (data.dateDebut != null) updates.push('date_debut = \'' + esc(data.dateDebut) + '\'');
if (data.dateFin != null) updates.push('date_fin = \'' + esc(data.dateFin) + '\'');
if (data.joursPris != null) updates.push('jours_pris = ' + esc(data.joursPris));
if (data.motif != null) updates.push('motif = \'' + esc(data.motif) + '\'');
if (data.statut != null) updates.push('statut = \'' + esc(data.statut) + '\'');
if (data.approuvePar != null) updates.push('approuve_par = \'' + esc(data.approuvePar) + '\'');
if (data.dateReponse != null) updates.push('date_reponse = \'' + esc(data.dateReponse) + '\'');
if (data.commentaire != null) updates.push('commentaire = \'' + esc(data.commentaire) + '\'');

if (updates.length === 0) {
  throw new Error('No fields to update');
}

var query = 'UPDATE rh_conges SET ' + updates.join(', ') + ' WHERE id = \'' + esc(id) + '\' AND tenant_id = \'' + esc(tenantId) + '\' RETURNING *';

return {query: query};

var row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, message: 'Not found'}}];
}

var item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  joursPris: Number(row.jours_pris || 0),
  motif: row.motif,
  statut: row.statut,
  approuvePar: row.approuve_par,
  dateReponse: row.date_reponse,
  commentaire: row.commentaire,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: item}}];
