var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var memberId = data.memberId || null;
var type = data.type || null;
var statut = data.statut || null;
var dateFrom = data.dateFrom || null;
var dateTo = data.dateTo || null;

var whereClauses = ['tenant_id = \'\' + esc(tenantId) + '\''];
if (memberId) whereClauses.push('member_id = \'\' + esc(memberId) + '\'');
if (type) whereClauses.push('type = \'\' + esc(type) + '\'');
if (statut) whereClauses.push('statut = \'\' + esc(statut) + '\'');
if (dateFrom) whereClauses.push('date_debut >= \'\' + esc(dateFrom) + '\'');
if (dateTo) whereClauses.push('date_fin <= \'\' + esc(dateTo) + '\'');

var query = 'SELECT c.*, em.prenom || \' \' || em.nom as membreNom FROM rh_conges c LEFT JOIN equipe_members em ON c.member_id = em.id WHERE ' + whereClauses.join(' AND ') + ' ORDER BY c.date_debut DESC';

return {query: query};

const rows = $input.first().json.queryResults || [];
var items = rows.map(function(row) {
  return {
    id: row.id,
    tenantId: row.tenant_id,
    memberId: row.member_id,
    membreNom: row.membreNom,
    type: row.type,
    dateDebut: row.date_debut,
    dateFin: row.date_fin,
    joursPris: Number(row.jours_pris || 0),
    motif: row.motif,
    statut: row.statut,
    approuvePar: row.approuve_par,
    dateReponse: row.date_reponse,
    commentaire: row.commentaire,
    createdAt: row.created_at,
    updatedAt: row.updated_at
  };
});

return [{json: {success: true, data: items}}];
