var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var id = require('crypto').randomUUID();
var now = new Date().toISOString();
var memberId = data.memberId || '';
var type = data.type || '';
var statut = data.statut || 'actif';
var dateDebut = data.dateDebut || '';
var dateFin = data.dateFin || null;
var salaireBase = data.salaireBase || 0;
var poste = data.poste || '';
var departement = data.departement || '';
var avantages = data.avantages || '';
var clauseParticuliere = data.clauseParticuliere || '';
var motifFin = data.motifFin || '';

var query = "INSERT INTO rh_contrats (id, tenant_id, member_id, type, statut, date_debut, date_fin, salaire_base, poste, departement, avantages, clause_particuliere, motif_fin, created_at, updated_at) VALUES ('" + esc(id) + "', '" + esc(tenantId) + "', '" + esc(memberId) + "', '" + esc(type) + "', '" + esc(statut) + "', '" + esc(dateDebut) + "', " + (dateFin ? "'" + esc(dateFin) + "'" : "NULL") + ", " + salaireBase + ", '" + esc(poste) + "', '" + esc(departement) + "', '" + esc(avantages) + "', '" + esc(clauseParticuliere) + "', '" + esc(motifFin) + "', '" + esc(now) + "', '" + esc(now) + "') RETURNING *;";

return [{json: {query: query}}];

const row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, error: 'Creation failed'}}];
}
return [{json: {success: true, data: {
  id: row.id,
  memberId: row.member_id,
  type: row.type,
  statut: row.statut,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  salaireBase: Number(row.salaire_base || 0),
  poste: row.poste,
  departement: row.departement,
  avantages: row.avantages,
  clauseParticuliere: row.clause_particuliere,
  motifFin: row.motif_fin,
  createdAt: row.created_at,
  updatedAt: row.updated_at
}}}];
