var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var whereParts = ["c.tenant_id = '" + esc(tenantId) + "'"];

if (data.membreId && data.membreId.trim()) {
  whereParts.push("c.member_id = '" + esc(data.membreId) + "'");
}
if (data.type && data.type.trim()) {
  whereParts.push("c.type = '" + esc(data.type) + "'");
}
if (data.statut && data.statut.trim()) {
  whereParts.push("c.statut = '" + esc(data.statut) + "'");
}

var query = "SELECT c.*, m.nom as membre_nom, m.prenom as membre_prenom FROM rh_contrats c LEFT JOIN equipe_members m ON c.member_id = m.id WHERE " + whereParts.join(' AND ') + " ORDER BY c.created_at DESC;";

return [{json: {query: query}}];
