var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var tid = esc(tenantId);
var query = "SELECT (SELECT COUNT(*) FROM equipe_members WHERE tenant_id = '" + tid + "' AND actif = true) as effectif_total, (SELECT COUNT(*) FROM rh_contrats WHERE tenant_id = '" + tid + "' AND statut = 'actif') as contrats_actifs, (SELECT COUNT(*) FROM rh_conges WHERE tenant_id = '" + tid + "' AND statut = 'en_attente') as conges_en_attente, (SELECT COUNT(*) FROM rh_formations WHERE tenant_id = '" + tid + "' AND statut = 'en_cours') as formations_en_cours, (SELECT COUNT(*) FROM rh_evaluations WHERE tenant_id = '" + tid + "' AND DATE_TRUNC('month', created_at) = DATE_TRUNC('month', NOW())) as evaluations_ce_mois, (SELECT COALESCE(SUM(salaire_base), 0) FROM rh_contrats WHERE tenant_id = '" + tid + "' AND statut = 'actif') as masse_salariale, (SELECT COALESCE(COUNT(*) FILTER (WHERE statut = 'approuve' AND date_debut <= NOW() AND date_fin >= NOW()) * 100.0 / NULLIF((SELECT COUNT(*) FROM equipe_members WHERE tenant_id = '" + tid + "' AND actif = true), 0), 0) FROM rh_conges WHERE tenant_id = '" + tid + "') as taux_absenteisme";

return { query: query };

var r = $input.first().json;
var result = {
  effectifTotal: Number(r.effectif_total || 0),
  contratsActifs: Number(r.contrats_actifs || 0),
  congesEnAttente: Number(r.conges_en_attente || 0),
  formationsEnCours: Number(r.formations_en_cours || 0),
  evaluationsCeMois: Number(r.evaluations_ce_mois || 0),
  masseSalariale: Number(r.masse_salariale || 0),
  tauxAbsenteisme: Number(r.taux_absenteisme || 0)
};
return [{json: { success: true, data: result }}];
