var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

if (!data.memberId || !data.type || !data.titre) {
  throw new Error('memberId, type, titre are required');
}

var query = 'INSERT INTO rh_documents (tenant_id, member_id, type, titre, description, fichier_url, taille_fichier) VALUES (' +
  '\'' + esc(tenantId) + '\',' +
  '\'' + esc(data.memberId) + '\',' +
  '\'' + esc(data.type) + '\',' +
  '\'' + esc(data.titre) + '\',' +
  (data.description ? '\'' + esc(data.description) + '\'' : 'NULL') + ',' +
  (data.fichierUrl ? '\'' + esc(data.fichierUrl) + '\'' : 'NULL') + ',' +
  (data.tailleFichier != null ? esc(data.tailleFichier) : 'NULL') +
  ') RETURNING *';

return {query: query};

var row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, message: 'Creation failed'}}];
}

var item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  titre: row.titre,
  description: row.description,
  fichierUrl: row.fichier_url,
  tailleFichier: row.taille_fichier,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: item}}];
