var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var id = String(data.id || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

if (!id) {
  throw new Error('id is required');
}

var updates = [];
if (data.memberId != null) updates.push('member_id = \'' + esc(data.memberId) + '\'');
if (data.type != null) updates.push('type = \'' + esc(data.type) + '\'');
if (data.titre != null) updates.push('titre = \'' + esc(data.titre) + '\'');
if (data.description != null) updates.push('description = \'' + esc(data.description) + '\'');
if (data.fichierUrl != null) updates.push('fichier_url = \'' + esc(data.fichierUrl) + '\'');
if (data.tailleFichier != null) updates.push('taille_fichier = ' + esc(data.tailleFichier));

if (updates.length === 0) {
  throw new Error('No fields to update');
}

var query = 'UPDATE rh_documents SET ' + updates.join(', ') + ' WHERE id = \'' + esc(id) + '\' AND tenant_id = \'' + esc(tenantId) + '\' RETURNING *';

return {query: query};

var row = $input.first().json;
if (!row || !row.id) {
  return [{json: {success: false, message: 'Not found'}}];
}

var item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  titre: row.titre,
  description: row.description,
  fichierUrl: row.fichier_url,
  tailleFichier: row.taille_fichier,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: item}}];
