var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var memberId = data.membreId ? esc(String(data.membreId)) : null;
var type = data.type ? esc(String(data.type)) : '';
var dateEntretien = data.dateEntretien ? esc(String(data.dateEntretien)) : null;
var evaluateur = data.evaluateur ? esc(String(data.evaluateur)) : '';
var compteRendu = data.compteRendu ? esc(String(data.compteRendu)) : '';
var objectifs = data.objectifs ? esc(String(data.objectifs)) : '';
var pointsForts = data.pointsForts ? esc(String(data.pointsForts)) : '';
var axesAmelioration = data.axesAmelioration ? esc(String(data.axesAmelioration)) : '';
var statut = data.statut ? esc(String(data.statut)) : 'en_cours';

if (!memberId) throw new Error('Missing membreId');
if (!dateEntretien) throw new Error('Missing dateEntretien');

var query = "INSERT INTO rh_entretiens (tenant_id, member_id, type, date_entretien, evaluateur, compte_rendu, objectifs, points_forts, axes_amelioration, statut) VALUES ('" + esc(tenantId) + "', '" + memberId + "', '" + type + "', '" + dateEntretien + "', '" + evaluateur + "', '" + compteRendu + "', '" + objectifs + "', '" + pointsForts + "', '" + axesAmelioration + "', '" + statut + "') RETURNING *";

return { query: query };

var r = $input.first().json;
if (!r || !r.id) {
  return [{json: { success: false, error: 'Failed to create entretien' }}];
}
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  membreId: r.member_id,
  type: r.type,
  dateEntretien: r.date_entretien,
  evaluateur: r.evaluateur,
  compteRendu: r.compte_rendu,
  objectifs: r.objectifs,
  pointsForts: r.points_forts,
  axesAmelioration: r.axes_amelioration,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return [{json: { success: true, data: result }}];
