var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var id = data.id ? esc(String(data.id)) : null;
if (!id) throw new Error('Missing id');

var updates = [];
if (data.type !== undefined) updates.push("type = '" + esc(String(data.type)) + "'");
if (data.dateEntretien !== undefined) updates.push("date_entretien = '" + esc(String(data.dateEntretien)) + "'");
if (data.evaluateur !== undefined) updates.push("evaluateur = '" + esc(String(data.evaluateur)) + "'");
if (data.compteRendu !== undefined) updates.push("compte_rendu = '" + esc(String(data.compteRendu)) + "'");
if (data.objectifs !== undefined) updates.push("objectifs = '" + esc(String(data.objectifs)) + "'");
if (data.pointsForts !== undefined) updates.push("points_forts = '" + esc(String(data.pointsForts)) + "'");
if (data.axesAmelioration !== undefined) updates.push("axes_amelioration = '" + esc(String(data.axesAmelioration)) + "'");
if (data.statut !== undefined) updates.push("statut = '" + esc(String(data.statut)) + "'");

if (updates.length === 0) throw new Error('No fields to update');

var query = "UPDATE rh_entretiens SET " + updates.join(', ') + ", updated_at = NOW() WHERE id = '" + id + "' AND tenant_id = '" + esc(tenantId) + "' RETURNING *";

return { query: query };

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: { success: false, error: 'Entretien not found' }}];
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  membreId: r.member_id,
  type: r.type,
  dateEntretien: r.date_entretien,
  evaluateur: r.evaluateur,
  compteRendu: r.compte_rendu,
  objectifs: r.objectifs,
  pointsForts: r.points_forts,
  axesAmelioration: r.axes_amelioration,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return [{json: { success: true, data: result }}];
