var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var memberId = data.membreId ? esc(String(data.membreId)) : null;
var type = data.type ? esc(String(data.type)) : null;
var dateFrom = data.dateFrom ? esc(String(data.dateFrom)) : null;
var dateTo = data.dateTo ? esc(String(data.dateTo)) : null;

var whereClause = "tenant_id = '" + esc(tenantId) + "'";
if (memberId) whereClause += " AND member_id = '" + memberId + "'";
if (type) whereClause += " AND type = '" + type + "'";
if (dateFrom) whereClause += " AND date_entretien >= '" + dateFrom + "'";
if (dateTo) whereClause += " AND date_entretien <= '" + dateTo + "'";

var query = "SELECT e.*, em.nom as membreNom FROM rh_entretiens e LEFT JOIN equipe_members em ON e.member_id = em.id WHERE " + whereClause + " ORDER BY e.date_entretien DESC";

return { query: query };

var rows = $input.first().json.rows || [];
var formatted = rows.map(function(r) {
  return {
    id: r.id,
    tenantId: r.tenant_id,
    membreId: r.member_id,
    membreNom: r.membreNom || '',
    type: r.type,
    dateEntretien: r.date_entretien,
    evaluateur: r.evaluateur,
    compteRendu: r.compte_rendu,
    objectifs: r.objectifs,
    pointsForts: r.points_forts,
    axesAmelioration: r.axes_amelioration,
    statut: r.statut,
    createdAt: r.created_at,
    updatedAt: r.updated_at
  };
});
return { success: true, data: formatted };
