var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var memberId = data.membreId ? esc(String(data.membreId)) : null;
var evaluateurId = data.evaluateurId ? esc(String(data.evaluateurId)) : null;
var periode = data.periode ? esc(String(data.periode)) : '';
var annee = data.annee ? esc(String(data.annee)) : '';
var noteGlobale = data.noteGlobale !== undefined ? data.noteGlobale : null;
var commentaires = data.commentaires ? esc(String(data.commentaires)) : '';
var objectifsAtteints = data.objectifsAtteints ? esc(String(data.objectifsAtteints)) : '';
var objectifsFuturs = data.objectifsFuturs ? esc(String(data.objectifsFuturs)) : '';
var competences = data.competences ? esc(String(data.competences)) : '';
var statut = data.statut ? esc(String(data.statut)) : 'en_cours';

if (!memberId) throw new Error('Missing membreId');

var query = "INSERT INTO rh_evaluations (tenant_id, member_id, evaluateur_id, periode, annee, note_globale, commentaires, objectifs_atteints, objectifs_futurs, competences, statut) VALUES ('" + esc(tenantId) + "', '" + memberId + "', '" + (evaluateurId || '') + "', '" + periode + "', '" + annee + "', " + (noteGlobale !== null ? noteGlobale : 'null') + ", '" + commentaires + "', '" + objectifsAtteints + "', '" + objectifsFuturs + "', '" + competences + "', '" + statut + "') RETURNING *";

return { query: query };

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: { success: false, error: 'Failed to create evaluation' }}];
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  membreId: r.member_id,
  evaluateurId: r.evaluateur_id,
  periode: r.periode,
  annee: r.annee,
  noteGlobale: Number(r.note_globale || 0),
  commentaires: r.commentaires,
  objectifsAtteints: r.objectifs_atteints,
  objectifsFuturs: r.objectifs_futurs,
  competences: r.competences,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return [{json: { success: true, data: result }}];
