var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var id = data.id ? esc(String(data.id)) : null;
if (!id) throw new Error('Missing id');

var updates = [];
if (data.evaluateurId !== undefined) updates.push("evaluateur_id = '" + esc(String(data.evaluateurId)) + "'");
if (data.periode !== undefined) updates.push("periode = '" + esc(String(data.periode)) + "'");
if (data.annee !== undefined) updates.push("annee = '" + esc(String(data.annee)) + "'");
if (data.noteGlobale !== undefined) updates.push("note_globale = " + (data.noteGlobale !== null ? data.noteGlobale : 'null'));
if (data.commentaires !== undefined) updates.push("commentaires = '" + esc(String(data.commentaires)) + "'");
if (data.objectifsAtteints !== undefined) updates.push("objectifs_atteints = '" + esc(String(data.objectifsAtteints)) + "'");
if (data.objectifsFuturs !== undefined) updates.push("objectifs_futurs = '" + esc(String(data.objectifsFuturs)) + "'");
if (data.competences !== undefined) updates.push("competences = '" + esc(String(data.competences)) + "'");
if (data.statut !== undefined) updates.push("statut = '" + esc(String(data.statut)) + "'");

if (updates.length === 0) throw new Error('No fields to update');

var query = "UPDATE rh_evaluations SET " + updates.join(', ') + ", updated_at = NOW() WHERE id = '" + id + "' AND tenant_id = '" + esc(tenantId) + "' RETURNING *";

return { query: query };

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: { success: false, error: 'Evaluation not found' }}];
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  membreId: r.member_id,
  evaluateurId: r.evaluateur_id,
  periode: r.periode,
  annee: r.annee,
  noteGlobale: Number(r.note_globale || 0),
  commentaires: r.commentaires,
  objectifsAtteints: r.objectifs_atteints,
  objectifsFuturs: r.objectifs_futurs,
  competences: r.competences,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return [{json: { success: true, data: result }}];
