var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var memberId = data.membreId ? esc(String(data.membreId)) : null;
var periode = data.periode ? esc(String(data.periode)) : null;
var annee = data.annee ? esc(String(data.annee)) : null;

var whereClause = "ev.tenant_id = '" + esc(tenantId) + "'";
if (memberId) whereClause += " AND ev.member_id = '" + memberId + "'";
if (periode) whereClause += " AND ev.periode = '" + periode + "'";
if (annee) whereClause += " AND ev.annee = '" + annee + "'";

var query = "SELECT ev.*, em.nom as membreNom, evaluateur.nom as evaluateurNom FROM rh_evaluations ev LEFT JOIN equipe_members em ON ev.member_id = em.id LEFT JOIN equipe_members evaluateur ON ev.evaluateur_id = evaluateur.id WHERE " + whereClause + " ORDER BY ev.annee DESC, ev.periode";

return { query: query };

const rows = $input.first().json.queryResults || [];
var formatted = rows.map(function(r) {
  return {
    id: r.id,
    tenantId: r.tenant_id,
    membreId: r.member_id,
    membreNom: r.membreNom || '',
    evaluateurId: r.evaluateur_id,
    evaluateurNom: r.evaluateurNom || '',
    periode: r.periode,
    annee: r.annee,
    noteGlobale: Number(r.note_globale || 0),
    commentaires: r.commentaires,
    objectifsAtteints: r.objectifs_atteints,
    objectifsFuturs: r.objectifs_futurs,
    competences: r.competences,
    statut: r.statut,
    createdAt: r.created_at,
    updatedAt: r.updated_at
  };
});
return [{json: { success: true, data: formatted }}];
