var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var titre = data.titre ? esc(String(data.titre)) : null;
var description = data.description ? esc(String(data.description)) : '';
var formateur = data.formateur ? esc(String(data.formateur)) : '';
var organisme = data.organisme ? esc(String(data.organisme)) : '';
var dateDebut = data.dateDebut ? esc(String(data.dateDebut)) : null;
var dateFin = data.dateFin ? esc(String(data.dateFin)) : null;
var cout = data.cout !== undefined ? data.cout : 0;
var maxParticipants = data.maxParticipants !== undefined ? data.maxParticipants : null;
var lieu = data.lieu ? esc(String(data.lieu)) : '';
var statut = data.statut ? esc(String(data.statut)) : 'planifiee';

if (!titre) throw new Error('Missing titre');
if (!dateDebut) throw new Error('Missing dateDebut');

var query = "INSERT INTO rh_formations (tenant_id, titre, description, formateur, organisme, date_debut, date_fin, cout, max_participants, lieu, statut) VALUES ('" + esc(tenantId) + "', '" + titre + "', '" + description + "', '" + formateur + "', '" + organisme + "', '" + dateDebut + "', '" + dateFin + "', " + cout + ", " + (maxParticipants !== null ? maxParticipants : 'null') + ", '" + lieu + "', '" + statut + "') RETURNING *";

return { query: query };

var rows = $input.first().json.rows || [];
if (rows.length === 0) {
  return { success: false, error: 'Failed to create formation' };
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  titre: r.titre,
  description: r.description,
  formateur: r.formateur,
  organisme: r.organisme,
  dateDebut: r.date_debut,
  dateFin: r.date_fin,
  cout: r.cout,
  maxParticipants: r.max_participants,
  participantsInscrits: 0,
  lieu: r.lieu,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return { success: true, data: result };
