var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var formationId = data.id ? esc(String(data.id)) : null;
var membreId = data.membreId ? esc(String(data.membreId)) : null;

if (!formationId) throw new Error('Missing formation id');
if (!membreId) throw new Error('Missing membreId');

var query = "INSERT INTO rh_inscriptions_formations (tenant_id, formation_id, member_id, date_inscription) VALUES ('" + esc(tenantId) + "', '" + formationId + "', '" + membreId + "', NOW()) RETURNING *";

return { query: query };

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: { success: false, error: 'Failed to register member for formation' }}];
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  formationId: r.formation_id,
  membreId: r.member_id,
  dateInscription: r.date_inscription,
  presence: r.presence,
  noteFormation: r.note_formation,
  commentaire: r.commentaire
};
return [{json: { success: true, data: result }}];
