var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var id = data.id ? esc(String(data.id)) : null;
if (!id) throw new Error('Missing id');

var updates = [];
if (data.titre !== undefined) updates.push("titre = '" + esc(String(data.titre)) + "'");
if (data.description !== undefined) updates.push("description = '" + esc(String(data.description)) + "'");
if (data.formateur !== undefined) updates.push("formateur = '" + esc(String(data.formateur)) + "'");
if (data.organisme !== undefined) updates.push("organisme = '" + esc(String(data.organisme)) + "'");
if (data.dateDebut !== undefined) updates.push("date_debut = '" + esc(String(data.dateDebut)) + "'");
if (data.dateFin !== undefined) updates.push("date_fin = '" + esc(String(data.dateFin)) + "'");
if (data.cout !== undefined) updates.push("cout = " + data.cout);
if (data.maxParticipants !== undefined) updates.push("max_participants = " + (data.maxParticipants !== null ? data.maxParticipants : 'null'));
if (data.lieu !== undefined) updates.push("lieu = '" + esc(String(data.lieu)) + "'");
if (data.statut !== undefined) updates.push("statut = '" + esc(String(data.statut)) + "'");

if (updates.length === 0) throw new Error('No fields to update');

var query = "UPDATE rh_formations SET " + updates.join(', ') + ", updated_at = NOW() WHERE id = '" + id + "' AND tenant_id = '" + esc(tenantId) + "' RETURNING *";

return { query: query };

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: { success: false, error: 'Formation not found' }}];
}
var r = rows[0];
var result = {
  id: r.id,
  tenantId: r.tenant_id,
  titre: r.titre,
  description: r.description,
  formateur: r.formateur,
  organisme: r.organisme,
  dateDebut: r.date_debut,
  dateFin: r.date_fin,
  cout: r.cout,
  maxParticipants: r.max_participants,
  lieu: r.lieu,
  statut: r.statut,
  createdAt: r.created_at,
  updatedAt: r.updated_at
};
return [{json: { success: true, data: result }}];
