var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var statut = data.statut ? esc(String(data.statut)) : null;
var dateFrom = data.dateFrom ? esc(String(data.dateFrom)) : null;
var dateTo = data.dateTo ? esc(String(data.dateTo)) : null;

var whereClause = "tenant_id = '" + esc(tenantId) + "'";
if (statut) whereClause += " AND statut = '" + statut + "'";
if (dateFrom) whereClause += " AND date_debut >= '" + dateFrom + "'";
if (dateTo) whereClause += " AND date_fin <= '" + dateTo + "'";

var query = "SELECT f.*, (SELECT COUNT(*) FROM rh_inscriptions_formations WHERE formation_id = f.id) as participants_inscrits FROM rh_formations f WHERE " + whereClause + " ORDER BY f.date_debut DESC";

return { query: query };

var rows = $input.all().map(function(i) { return i.json; }).filter(function(i) { return i.id; });
var formatted = rows.map(function(r) {
  return {
    id: r.id,
    tenantId: r.tenant_id,
    titre: r.titre,
    description: r.description,
    formateur: r.formateur,
    organisme: r.organisme,
    dateDebut: r.date_debut,
    dateFin: r.date_fin,
    cout: Number(r.cout || 0),
    maxParticipants: Number(r.max_participants || 0),
    participantsInscrits: Number(r.participants_inscrits || 0),
    lieu: r.lieu,
    statut: r.statut,
    createdAt: r.created_at,
    updatedAt: r.updated_at
  };
});
return [{json: { success: true, data: formatted }}];
