var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

var whereParts = ["b.tenant_id = '" + esc(tenantId) + "'"];

if (data.membreId && data.membreId.trim()) {
  whereParts.push("b.member_id = '" + esc(data.membreId) + "'");
}
if (data.mois !== undefined && data.mois !== null && data.mois !== '') {
  whereParts.push("b.mois = " + parseInt(data.mois));
}
if (data.annee !== undefined && data.annee !== null && data.annee !== '') {
  whereParts.push("b.annee = " + parseInt(data.annee));
}

var query = "SELECT b.*, m.nom as membre_nom, m.prenom as membre_prenom FROM rh_bulletins_paie b LEFT JOIN equipe_members m ON b.member_id = m.id WHERE " + whereParts.join(' AND ') + " ORDER BY b.annee DESC, b.mois DESC;";

return [{json: {query: query}}];
